CREATE FUNCTION dashboard_permissions_private.app_permissions_bitnum_tg (  ) RETURNS trigger AS $EOFCODE$
DECLARE
    bitlen int = bit_length(NEW.bitstr);
BEGIN
    NEW.bitstr = 
        lpad('', bitlen - NEW.bitnum, '0') ||
	    '1' ||
	    lpad('', NEW.bitnum - 1, '0');
    RETURN NEW;
END;
$EOFCODE$ LANGUAGE plpgsql VOLATILE;

