INSERT INTO dashboard_public.organization_profiles ( organization_id, name, website, description ) VALUES ('1e68800d-f85c-5d42-b439-8523700e1572', 'Dashboard.Earth', 'http://www.dashboard.earth', 'Our mission is to connect everyone to the solution necessary to balance the climate'), ('22196e48-51e3-5791-835a-1ffa40477f7b', 'Kiss the Ground', 'https://kisstheground.com', 'Inspiring participation in global regeneration, starting with soil.'), ('0c35d658-b6a5-59b4-a455-6a0635a7a133', 'Carbon Underground', 'https://thecarbonunderground.org', 'The Carbon Underground was created to educate the world about the power of healthy soil to combat climate change and to facilitate the transition of enough farms and grasslands globally to restore a healthy climate.'), ('e4720bb1-7546-5d1a-bf21-a7e2ddcfc576', 'TreePeople', 'https://www.treepeople.org', 'TreePeople''s mission is to inspire, engage and support people to take personal responsibility for the urban environment, making it safe, healthy, fun and sustainable.'), ('9f0838d3-abc3-579d-9588-ec15ebfa0a50', 'Nori', 'https://nori.com', 'Nori is on a mission to reverse climate change. Our goal is clear: create a new way for anyone in the world to pay to remove excess carbon dioxide from the atmosphere.'), ('eef406c3-4d51-50ef-8427-51e9757ff6c9', 'Community Healing Gardens', 'https://www.communityhealinggardens.org', 'Community Healing Gardens is the only urban gardening program building community, utilizing clean technologies to grow, while providing food for people of all socio-economic status from Venice to Watts.'), ('47bc3efc-6c80-5de5-b084-e5b1e11b4e29', 'Buckminister Fuller Institute', 'https://www.bfi.org', 'The Buckminster Fuller Institute is dedicated to accelerating the development and deployment of solutions which radically advance human well being.'), ('0dca7b2f-573e-5395-bbd0-c4da5c9c35c5', 'Yoga Collective', 'https://www.theyogacollective.com', 'From Vinyasa Flow Yoga to Meditation from Life Coaching to Fresh Recipes, we have a wide variety of videos at your fingertips.'), ('a36225fd-1ed3-5506-b8bc-51687255c35c', 'Alliance for Climate Education', NULL, 'We educate young people on the science of climate change and empower them to take action.'), ('036cf352-3433-5b92-b1fb-123d6f8a974e', 'City Plants', 'https://www.cityplants.org', 'Our Mission
Our mission is to grow a greener future for Los Angeles by engaging Angelenos to plant and care for trees throughout the City.
Our Vision
At City Plants, we envision a Los Angeles in which people in every neighborhood have equal access to trees and their benefits: clean air, energy efficiency, better health, cooling shade, and friendlier, more vibrant communities.'), ('943235fa-7884-5cf2-a3ba-09227022ba74', 'LAAudubon', 'https://www.laaudubon.org', 'Explore. Learn. Restore.
Los Angeles Audubon Society works to promote the  enjoyment and protection of birds and other wildlife in the Los Angeles  area through outdoor education programs, bird friendly activities, and  hands-on conservation initiatives.'), ('3853ce85-e466-547d-a4e8-f686dc6f368a', 'California’s native plants Society', 'https://www.cnps.org', 'We’re on a mission to save California’s native plants and places using both head and heart. CNPS brings together science, education, conservation, and gardening to power the native plant movement.'), ('53bc27ed-4e11-525f-84e0-287e05980510', 'Arcadia Power', 'https://www.arcadia.com', 'Our local wind and solar farms.
