CREATE FUNCTION dashboard_private.tg_ensure_approved_actions (  ) RETURNS trigger AS $EOFCODE$
BEGIN
    IF (NEW.is_approved IS FALSE) THEN 
        NEW.is_published = FALSE;
    END IF;
 RETURN NEW;
END;
$EOFCODE$ LANGUAGE plpgsql VOLATILE;

