CREATE FUNCTION dashboard_private.tg_remove_saved_action (  ) RETURNS trigger AS $EOFCODE$
DECLARE
BEGIN
    IF (NEW.complete = TRUE AND NEW.verified = TRUE) THEN 
        DELETE FROM dashboard_public.user_saved_actions s 
            WHERE s.user_id = NEW.user_id AND s.action_id = NEW.action_id;
    END IF;

    RETURN NEW;
END;
$EOFCODE$ LANGUAGE plpgsql VOLATILE;

CREATE TRIGGER remove_saved_action_on_insert 
 AFTER UPDATE ON dashboard_public.user_actions 
 FOR EACH ROW
 WHEN ( NEW.complete IS DISTINCT FROM OLD.complete ) 
 EXECUTE PROCEDURE dashboard_private. tg_remove_saved_action (  );

CREATE TRIGGER remove_saved_action_on_update 
 AFTER INSERT ON dashboard_public.user_actions 
 FOR EACH ROW
 EXECUTE PROCEDURE dashboard_private. tg_remove_saved_action (  );

CREATE FUNCTION dashboard_private.tg_verify_user_action (  ) RETURNS trigger AS $EOFCODE$
BEGIN
    IF (
        SELECT self_verifiable IS TRUE
            FROM dashboard_public.actions a
        WHERE a.id = NEW.action_id
    ) THEN 
        NEW.verified = TRUE;
        NEW.verified_date = CURRENT_TIMESTAMP;
    ELSEIF (TG_OP = 'INSERT') THEN 
            NEW.verified = FALSE;
    END IF;
    RETURN NEW;
END;
$EOFCODE$ LANGUAGE plpgsql VOLATILE;

CREATE TRIGGER verify_on_insert 
 BEFORE INSERT ON dashboard_public.user_actions 
 FOR EACH ROW
 WHEN ( NEW.complete IS TRUE ) 
 EXECUTE PROCEDURE dashboard_private. tg_verify_user_action (  );

CREATE TRIGGER verify_on_update 
 BEFORE UPDATE ON dashboard_public.user_actions 
 FOR EACH ROW
 WHEN ( new.complete <> old.complete AND new.verified IS FALSE ) 
 EXECUTE PROCEDURE dashboard_private. tg_verify_user_action (  );

